import React ,{useState} from "react" ;

const Form =()=>{  
 /* use state mai space do aur name placeholder
  observe karo */   
   const [Name , setName ] =  useState({
       FName : "" , 
       LName : "" , 
       Email : "" , 
       Contact : "" 
   }); 

    
   const inputData = (event)=> {

       const value = event.target.value ;
       const name = event.target.name ;



       setName( (preValue)=> {
           return { ...preValue , 
                    [name] : value 
                 } ; 
        }
    );
 }
           
